import"reflect-metadata";import{Router}from"express";import{container}from"tsyringe";import{LiveService}from"../../application/services/lives.service.js";import{HttpStatus}from"../utils/http-status.js";class livesController{_router=Router();livesService=container.resolve(LiveService);constructor(){this._configure()}_configure(){this._router.get("/",(async(t,e,r)=>{})),this._router.post("/addlive",(async(t,e,r)=>{try{let t="cams_asmr",r="tiktok";await this.livesService.getStreamerLives(t,r);e.send({status:"ok"})}catch(t){e.status(HttpStatus.BADREQUEST).json({statusCode:HttpStatus.BADREQUEST,error:t.message,stack:t.stack})}}))}get router(){return this._router}}export default(new livesController).router;