import jwt from"jsonwebtoken";import{config}from"../../application/config/index.js";const secretKey=config.auth.secret,espiresin=config.auth.expiresIn;export async function generateJWT(e,t){const r={id:e,username:t},i={expiresIn:espiresin};var n=jwt.sign(r,secretKey,i);return await n}export async function validateJWT(e){try{return{isValid:!0,payload:jwt.verify(e,secretKey)}}catch(e){return{isValid:!1}}}export async function decodeJWT(e){try{return{isValid:!0,payload:jwt.verify(e,secretKey)}}catch(e){return{isValid:!1}}}